import React, {Component} from 'react' 
import {Listwrap} from './styledele'
import {urlChange} from 'component/script/imgurl'
import { withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
const mapState = state => {
    return{
        rest:state.detail.rest
    }
}
class List extends Component{
    constructor(){
        super()
        this.state = {
            noShow: true
        }
        this.showactive = (e)=>{
            e.stopPropagation();
            if(this.state.noShow){
                this.setState({
                    noShow: false
                })
            }
            else{
                this.setState({
                    noShow: true
                })
            }
            
        }
    }
    handleClick = (id)=>{
        this.props.history.push('/detail/'+id)
    }
    render(){
        if(this.props.data){
            let data = this.props.data.restaurant
            let image_path = urlChange(data.image_path,'?x-oss-process=image/format,webp/resize,w_130,h_130,m_fixed')
            let distance = data.distance<1000? data.distance+'m':(data.distance/1000).toFixed(2)+'km'
            let activity = data.activities.concat(data.supports)
            return(
                <Listwrap onClick = { ()=>{
                    this.handleClick(data.id)
                } }>
                <div className="index-shopInfo_1RRTZ0d">
                        <div className="logo-main_21aInWJ_0">
                            <img src={image_path} alt={data.name}/>
                            {
                                this.props.rest[data.id]?<span className="logo-categoryQuantity_uaoPHzl_0">{this.props.rest[data.id].restaurant_count}</span>:null
                            }
                            
                        </div>
                        <div className="index-main_N3FfcSz">
                            <div>
                                <h3>{data.name}</h3>
                                <ul>
                                    <li></li>
                                </ul>
                            </div>
                            <div className="index-line_2-iYR1A">
                                <div>
                                    <div className="starfix">
                                        <div className="starbg">
                                            <img src="data:image/svg+xml;base64," alt="score"/>
                                        </div>
                                        <div className="star" style={{width:`${data.rating*20}%`}}>
                                            <img src="data:image/svg+xml;base64," alt="score"/>
                                        </div>
                                    </div>
                                    <span className="score">{data.rating}</span>
                                    <span>月售{data.recent_order_num}单</span>
                                </div>
                            </div>
                            <div className="index-line_2-iYR1A">
                                <div className="index-line_2-iYR1A_left">
                                    <span>￥{data.float_minimum_order_amount}起送</span>
                                    <span className="columnline">{data.piecewise_agent_fee.description}</span>
                                </div>
                                <div className="index-line_2-iYR1A_right">
                                    <span>{distance}</span>
                                    <span className="columnline">{data.order_lead_time}分钟</span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="index-activityWrap_3mo1GyG">
                        <section className="index-tagLine_1rJw_lq">
                            {
                                data.support_tags.map((v,i)=>(
                                    <span key={i} className="spanWrap">{v.text}
                                        <span className="spancon" style={{border:`1px solid `}}> {v.text}</span>
                                    </span>
                                ))
                            }
                        </section>
                        <span className="line">
                            <img alt="" src="data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjgwIiBoZWlnaHQ9IjEiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PHBhdGggZD0iTTAgLjI1TDI4MCAwIiBzdHJva2U9IiNDQ0MiIHN0cm9rZS13aWR0aD0iLjUiIGZpbGw9Im5vbmUiIHN0cm9rZS1kYXNoYXJyYXk9IjEiLz48L3N2Zz4="/>
                        </span>
                        <section className="index-activities_25AUDsx">
                            <div className="index-activityList_1wvwwUY">
                            {
                                activity.map((v,i) => (
                                    <div className="index-actRow_2f_uNNA" key={v.id} style={i>1&&this.state.noShow?{display:'none'}:{display:'flex'}}>
                                        <span><span className="tags" style={{ background:`#${v.icon_color}`,color:'#fff'}}>{v.icon_name}</span></span>
                                        <span className="tagcon">{v.tips?v.tips:v.description}</span>
                                    </div>
                                ))
                            }
                            </div>
                            <div className="index-activityBtn_tMk-e1C" onClick={(e) => {this.showactive(e)}}>
                                <span>{data.activities.length+data.supports.length}个活动</span>
                                <img alt="" className={this.state.noShow?'':'trans'} src="data:image/svg+xml;base64,"/>
                            </div>
                        </section>
                    </div>
                </Listwrap>
            )
        }
        else{
            return null
        }
    }
}
export default connect(mapState)(withRouter(List))
